import React, { useState } from 'react';
import axios from 'axios';

export default function Login({ onLogin, api }){
  const [email, setEmail] = useState('candidate@example.com');
  const [password, setPassword] = useState('Password123');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function submit(e){
    e.preventDefault();
    setError(null);
    setLoading(true);
    try{
      const res = await axios.post(api + '/api/login', { email, password });
      const { token, user } = res.data;
      axios.defaults.headers.common['Authorization'] = 'Bearer ' + token;
      onLogin(token, user);
    } catch (err){
      setError(err.response?.data?.error || err.message);
    } finally { setLoading(false); }
  }

  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="w-full max-w-md bg-white p-8 rounded shadow">
        <h2 className="text-2xl font-semibold mb-4">Sign in</h2>
        <form onSubmit={submit} className="space-y-4">
          <div>
            <label className="block text-sm">Email</label>
            <input className="mt-1 p-2 border w-full rounded" value={email} onChange={e=>setEmail(e.target.value)} required />
          </div>
          <div>
            <label className="block text-sm">Password</label>
            <input type="password" className="mt-1 p-2 border w-full rounded" value={password} onChange={e=>setPassword(e.target.value)} required />
          </div>
          {error && <div className="text-sm text-red-600">{error}</div>}
          <div className="flex justify-between items-center">
            <button className="bg-sky-600 text-white px-4 py-2 rounded" disabled={loading}>{loading ? 'Signing...' : 'Sign in'}</button>
          </div>
        </form>
        <div className="mt-4 text-xs text-gray-500">Seeded account: <strong>candidate@example.com</strong> / <strong>Password123</strong></div>
      </div>
    </div>
  );
}
