import React, { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { useMap } from "react-leaflet";

const IPV4_REGEX =
  /^(25[0-5]|2[0-4]\d|1?\d{1,2})(\.(25[0-5]|2[0-4]\d|1?\d{1,2})){3}$/;
const IPV6_REGEX = /^([0-9a-fA-F]{1,4}:){7}[0-9a-fA-F]{1,4}$/;

export default function Home({ api, user, onLogout }) {
  const [geo, setGeo] = useState(null);
  const [query, setQuery] = useState("");
  const [history, setHistory] = useState([]);
  const [selected, setSelected] = useState({});
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadCurrent();
    fetchHistory();
  }, []);

  function RecenterMap({ lat, lng }) {
    const map = useMap();
    useEffect(() => {
      if (lat && lng) {
        map.setView([lat, lng]);
      }
    }, [lat, lng]);
    return null;
  }

  async function loadCurrent() {
    try {
      setLoading(true);
      const res = await axios.get(api + "/api/geo");
      setGeo(res.data);
    } catch (err) {
      setError("Failed to load geo");
    } finally {
      setLoading(false);
    }
  }

  async function fetchHistory() {
    try {
      const res = await axios.get(api + "/api/history");
      setHistory(res.data.history || []);
    } catch (err) {
      /* ignore */
    }
  }

  const isValidIp = (s) => IPV4_REGEX.test(s) || IPV6_REGEX.test(s);

  async function handleSearch(e) {
    e && e.preventDefault();
    setError(null);
    if (!query) return;
    if (!isValidIp(query)) {
      setError("Not a valid IP address");
      return;
    }
    try {
      setLoading(true);
      const res = await axios.get(api + "/api/geo/" + query);
      setGeo(res.data);
      // save history
      await axios.post(api + "/api/history", { ip: query, data: res.data });
      fetchHistory();
    } catch (err) {
      setError("Lookup failed");
    } finally {
      setLoading(false);
    }
  }

  function clearSearch() {
    setQuery("");
    setError(null);
    loadCurrent();
  }

  function toggleSelect(id) {
    setSelected((s) => ({ ...s, [id]: !s[id] }));
  }
  async function deleteSelected() {
    const ids = Object.keys(selected).filter((k) => selected[k]);
    if (ids.length === 0) return;
    await axios.delete(api + "/api/history", { data: { ids } });
    setSelected({});
    fetchHistory();
  }

  // map coords
  const loc = geo?.loc ? geo.loc.split(",").map(Number) : null;

  return (
    <div className="min-h-screen p-6 bg-slate-50">
      <div className="max-w-5xl mx-auto bg-white rounded shadow p-6">
        <div className="flex justify-between items-center">
          <h1 className="text-xl font-semibold">
            IP Geolocation — Welcome, {user?.name}
          </h1>
          <div className="flex items-center space-x-2">
            <button className="px-3 py-1 border rounded" onClick={onLogout}>
              Logout
            </button>
          </div>
        </div>

        <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-2">
            <form onSubmit={handleSearch} className="flex gap-2">
              <input
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Enter IP (e.g. 8.8.8.8)"
                className="flex-1 p-2 border rounded"
              />
              <button className="px-3 py-2 bg-sky-600 text-white rounded">
                Lookup
              </button>
              <button
                type="button"
                onClick={clearSearch}
                className="px-3 py-2 border rounded"
              >
                Clear
              </button>
            </form>
            {error && <div className="text-sm text-red-600 mt-2">{error}</div>}

            <div className="mt-4">
              <h3 className="font-medium">Current Geo</h3>
              {geo ? (
                <pre className="bg-gray-50 p-3 rounded mt-2">
                  {JSON.stringify(geo, null, 2)}
                </pre>
              ) : (
                <div className="mt-2">Loading...</div>
              )}
            </div>

            <div className="mt-4">
              <h3 className="font-medium">Map</h3>
              {loc ? (
                <MapContainer
                  center={loc}
                  zoom={8}
                  scrollWheelZoom={false}
                  id="map"
                >
                  <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                  <Marker position={loc}>
                    <Popup>
                      {geo?.city}, {geo?.region}
                    </Popup>
                  </Marker>
                </MapContainer>
              ) : (
                <div className="mt-2">No coordinates available</div>
              )}
            </div>
          </div>

          <aside className="space-y-4">
            <div className="bg-white border p-3 rounded">
              <div className="flex justify-between items-center">
                <h4 className="font-medium">History</h4>
                <button
                  onClick={deleteSelected}
                  className="text-sm text-red-600"
                >
                  Delete selected
                </button>
              </div>
              <div className="mt-2 space-y-2 max-h-96 overflow-auto">
                {history.length === 0 && (
                  <div className="text-sm text-gray-500">No history</div>
                )}
                <ul className="space-y-2">
                  {history.map((item) => (
                    <li
                      key={item.id}
                      className="flex items-center justify-between p-2 border rounded"
                    >
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          checked={!!selected[item.id]}
                          onChange={() => toggleSelect(item.id)}
                        />
                        <button
                          className="ml-2 text-sky-600"
                          onClick={() => {
                            setQuery(item.ip);
                            handleSearch();
                          }}
                        >
                          {item.ip}
                        </button>
                      </div>
                      <div className="text-xs text-gray-500">
                        {new Date(item.when).toLocaleString()}
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </aside>
        </div>
      </div>
    </div>
  );
}
